import { MglMap, MglMarker } from "vue-mapbox";
export default {
  components: {
    MglMap,
    MglMarker
  },
  name: "HomeView",

  setup() {
    let map;
    onMounted(() => {
      // init map
      map = leaflet.map("mapid", {
        zoomControl: false
      }).setView([28.538336, -81.379234], 10); // add tile layers

      leaflet.tileLayer(`https://api.mapbox.com/styles/v1/{id}/tiles/{z}/{x}/{y}?access_token=${process.env.VUE_APP_API_KEY}`, {
        maxZoom: 18,
        id: "mapbox/streets-v11",
        tileSize: 512,
        zoomOffset: -1,
        accessToken: process.env.VUE_APP_API_KEY
      }).addTo(map);

      accessToken: `${process.env.VUE_APP_API_KEY}`;

      mapStyle: "style_object";

      map.on("moveend", () => {
        closeSearchResults();
      }); // get users location

      getGeolocation();
    });
    const coords = ref(null);
    const fetchCoords = ref(null);
    const geoMarker = ref(null);
    const geoError = ref(null);
    const geoErrorMsg = ref(null);

    const getGeolocation = () => {
      // if function is called, only run if we dont have coords
      if (!coords.value) {
        // check to see if we have coods in session sotrage
        if (sessionStorage.getItem("coords")) {
          coords.value = JSON.parse(sessionStorage.getItem("coords"));
          plotGeoLocation(coords.value);
          return;
        } // else get coords from geolocation API


        fetchCoords.value = true;
        navigator.geolocation.getCurrentPosition(setCoords, getLocError);
        return;
      } // otherwise, remove location


      coords.value = null;
      sessionStorage.removeItem("coords");
      map.removeLayer(geoMarker.value);
    };

    const setCoords = pos => {
      // stop fetching
      fetchCoords.value = null; // set coords in session storage

      const setSessionCoords = {
        lat: pos.latitude,
        lng: pos.longitude
      };
      sessionStorage.setItem("coords", JSON.stringify(setSessionCoords)); // set ref coords value

      coords.value = setSessionCoords;
      plotGeoLocation(coords.value);
    };

    const getLocError = error => {
      // stop fetching coords
      fetchCoords.value = null;
      geoError.value = true;
      geoErrorMsg.value = error.message;
    };

    const plotGeoLocation = coords => {
      // create custom marker
      const customMarker = leaflet.icon({
        iconUrl: require("../assets/map-marker-red.svg"),
        iconSize: [35, 35]
      }); // create new marker with coords and custom marker

      geoMarker.value = leaflet.marker([coords.lat, coords.lng], {
        icon: customMarker
      }).addTo(map); // set map view to current location

      map.setView([coords.lat, coords.lng], 10);
    };

    const resultMarker = ref(null);

    const plotResult = coords => {
      // If there is already a marker, remove it. Only allow 1
      if (resultMarker.value) {
        map.removeLayer(resultMarker.value);
      }

      const customMarker = leaflet.icon({
        iconUrl: require("../assets/map-marker-blue.svg"),
        iconSize: [35, 35] // size of the icon

      });
      resultMarker.value = leaflet.marker([coords.coordinates[1], coords.coordinates[0]], {
        icon: customMarker
      }).addTo(map);
      map.setView([coords.coordinates[1], coords.coordinates[0]], 13);
    };

    const removeResult = () => {
      map.removeLayer(resultMarker.value);
    };

    const closeGeoError = () => {
      geoErrorMsg.value = null;
      geoError.value = null;
    };

    const searchResults = ref(null);

    const toggleSearchResults = () => {
      searchResults.value = !searchResults.value;
    };

    const closeSearchResults = () => {
      searchResults.value = null;
    };

    return {
      geoError,
      closeGeoError,
      geoErrorMsg,
      fetchCoords,
      coords,
      getGeolocation,
      plotResult,
      searchResults,
      toggleSearchResults,
      closeSearchResults,
      removeResult,
      coordinates: [-111.549668, 39.014],
      locations: [{
        name: "Brushy Mountain State Penitentiary",
        date: "2019-10-26",
        coordinates: {
          latitude: 36.1052,
          longitude: -84.4529
        }
      }, {
        name: "Old South Pittsburg Hospital",
        date: "2019-11-02",
        coordinates: {
          latitude: 35.0021,
          longitude: -85.7147
        }
      }]
    };
  }

};
